<template>
  <div class="centered-container">
    <md-content class="md-elevation-3">

      <div class="title">
        <img src="../assets/logo.png">
        <div class="md-title">Buscador de usuários</div>
        <div class="md-body-1">Busque por qualquer usuário pelo nome completo ou username</div>
      </div>

      <div class="form">
        <md-field>
          <label>E-mail</label>
          <md-input v-model="login.email" autofocus></md-input>
        </md-field>

        <md-field md-has-password>
          <label>Password</label>
          <md-input v-model="login.password" type="password"></md-input>
        </md-field>
      </div>

      <div class="actions md-layout md-alignment-center-space-between">
        <span></span>
        <md-button class="md-raised md-primary" @click="auth">Log in</md-button>
      </div>

      <div class="loading-overlay" v-if="loading">
        <md-progress-spinner md-mode="indeterminate" :md-stroke="2"></md-progress-spinner>
      </div>

    </md-content>
    <div class="background" />

    <md-snackbar :md-position="snackbarPosition" :md-duration="snackbarDuration" :md-active.sync="showSnackbar" md-persistent>
      <span>{{snackbarMessage}}</span>
    </md-snackbar>
  </div>
</template>

<script>
export default {
  name: "App",
  data() {
    return {
      showSnackbar: false,
      snackbarPosition: 'center',
      snackbarDuration: 4000,
      snackbarMessage: "",
      loading: false,
      login: {
        email: "",
        password: ""
      }
    };
  },
  methods: {
    auth() {
      this.loading = true;
      this.$http.post('http://localhost:8000/auth/login', {email: this.login.email, password: this.login.password}).then(response => {
        localStorage.setItem('user-token', response.body.token)
        this.$router.replace({ name: "SearchUsers" });
        this.loading = false;
      }, response => {
        this.snackbarMessage = "Erro ao efetuar o login: "+ response.body.error;
        this.showSnackbar = true;
        this.loading = false;
      });
    }
  }
};
</script>

